  module.exports = function(url) {
    if (url == null) {
      console.log(url);
      url = '/bulma.css';
    }
    return function(req, res, next) {
      if (req.url === url) {
        return res.sendfile(__dirname + '/vendor/bulma.min.css');
      } else {
        return next();
      }
    };
  };

